// Write Transaction: 1 beat (AWLEN=0), 1 byte (AWSIZE=0), FIXED burst (AWBURST=0)
//--------------------------------------------------------------------------------------------
class axi4_master_bk_write_student_example_seq1 extends axi4_master_bk_base_seq;
  `uvm_object_utils(axi4_master_bk_write_student_example_seq1)

  //-------------------------------------------------------
  // Externally defined Tasks and Functions
