  super.new(name);
endfunction : new

//--------------------------------------------------------------------------------------------
// Task: body
// Creates the req of type master transaction and randomises the req
//--------------------------------------------------------------------------------------------
task axi4_master_bk_write_student_example_seq1::body();
  super.body();

  start_item(req);
  if(!req.randomize() with {req.awlen == 0;
                              req.awsize == WRITE_1_BYTE;
                              req.tx_type == WRITE;
                              req.transfer_type == BLOCKING_WRITE;
