//--------------------------------------------------------------------------------------------
// Class: axi4_virtual_student_example_32b_write_read_seq
// Creates and starts the master and slave sequences
// Student example test with specific constraints:
// - 2 write transactions (1 beat w/ 1 byte, 4 beats w/ 4 bytes, both FIXED burst)
// - 3 read transactions (1 beat w/ 1 byte FIXED, 2 beats w/ 4 bytes INCR,
//                        2 beats w/ 1 byte FIXED)
//--------------------------------------------------------------------------------------------
class axi4_virtual_student_example_32b_write_read_seq extends axi4_virtual_base_seq;
  `uvm_object_utils(axi4_virtual_student_example_32b_write_read_seq)

  //Variable: Master write sequence handles
  axi4_master_bk_write_student_example_seq1 axi4_master_bk_write_seq1_h;
  axi4_master_bk_write_student_example_seq2 axi4_master_bk_write_seq2_h;

  //Variable: Master read sequence handles
  axi4_master_bk_read_student_example_seq1 axi4_master_bk_read_seq1_h;
  axi4_master_bk_read_student_example_seq2 axi4_master_bk_read_seq2_h;
  axi4_master_bk_read_student_example_seq3 axi4_master_bk_read_seq3_h;

  //Variable: Slave sequence handles
  axi4_slave_bk_write_32b_transfer_seq axi4_slave_bk_write_32b_transfer_seq_h;
  axi4_slave_bk_read_32b_transfer_seq axi4_slave_bk_read_32b_transfer_seq_h;

  //-------------------------------------------------------
  // Externally defined Tasks and Functions
  //-------------------------------------------------------
  extern function new(string name = "axi4_virtual_student_example_32b_write_read_seq");
  extern task body();
endclass : axi4_virtual_student_example_32b_write_read_seq

//--------------------------------------------------------------------------------------------
// Construct: new
// Initialises new memory for the object
//
// Parameters:
//  name - axi4_virtual_student_example_32b_write_read_seq
//--------------------------------------------------------------------------------------------
function axi4_virtual_student_example_32b_write_read_seq::new(string name = "axi4_virtual_student_example_32b_write_read_seq");
  super.new(name);
endfunction : new

//--------------------------------------------------------------------------------------------
// Task - body
// Creates and starts the data of master and slave sequences
//--------------------------------------------------------------------------------------------
task axi4_virtual_student_example_32b_write_read_seq::body();

  axi4_master_bk_write_seq1_h = axi4_master_bk_write_student_example_seq1::type_id::create("axi4_master_bk_write_seq1_h");
  axi4_master_bk_write_seq2_h = axi4_master_bk_write_student_example_seq2::type_id::create("axi4_master_bk_write_seq2_h");

  axi4_master_bk_read_seq1_h = axi4_master_bk_read_student_example_seq1::type_id::create("axi4_master_bk_read_seq1_h");
  axi4_master_bk_read_seq2_h = axi4_master_bk_read_student_example_seq2::type_id::create("axi4_master_bk_read_seq2_h");
  axi4_master_bk_read_seq3_h = axi4_master_bk_read_student_example_seq3::type_id::create("axi4_master_bk_read_seq3_h");

  axi4_slave_bk_write_32b_transfer_seq_h = axi4_slave_bk_write_32b_transfer_seq::type_id::create("axi4_slave_bk_write_32b_transfer_seq_h");
  axi4_slave_bk_read_32b_transfer_seq_h = axi4_slave_bk_read_32b_transfer_seq::type_id::create("axi4_slave_bk_read_32b_transfer_seq_h");

  `uvm_info(get_type_name(), $sformatf("DEBUG :: Inside axi4_virtual_student_example_32b_write_read_seq"), UVM_NONE);

  fork
    begin : T1_SL_WR
      forever begin
        axi4_slave_bk_write_32b_transfer_seq_h.start(p_sequencer.axi4_slave_write_seqr_h);
      end
    end
    begin : T2_SL_RD
      forever begin
        axi4_slave_bk_read_32b_transfer_seq_h.start(p_sequencer.axi4_slave_read_seqr_h);
      end
    end
  join_none

  fork
    begin: T1_WRITE
      axi4_master_bk_write_seq1_h.start(p_sequencer.axi4_master_write_seqr_h);
      axi4_master_bk_write_seq2_h.start(p_sequencer.axi4_master_write_seqr_h);
    end
    begin: T2_READ
      axi4_master_bk_read_seq1_h.start(p_sequencer.axi4_master_read_seqr_h);
      axi4_master_bk_read_seq2_h.start(p_sequencer.axi4_master_read_seqr_h);
      axi4_master_bk_read_seq3_h.start(p_sequencer.axi4_master_read_seqr_h);
    end
  join
endtask : body

`endif
"}

